INSERT INTO `config_info` VALUES (140, 'server.yaml', 'DEFAULT_GROUP', 'spring:\n  aop:\n    proxy-target-class: true\n\n  jackson:\n    date-format: yyyy-MM-dd HH:mm:ss\n    time-zone: GMT+8\n\n  freemarker:\n    check-template-location: false\n\n  datasource:\n    dynamic:\n      p6spy: ${LOG_SQL_ENABLED:true}\n      strict: true\n      seata: true    #开启seata代理，开启后默认每个数据源都代理，如果某个不需要代理可单独关闭\n      seata-mode: AT #支持XA及AT模式,默认AT\n      hikari:\n        connection-timeout: 30000\n        max-lifetime: 1800000\n        max-pool-size: 15\n        min-idle: 5\n        connection-test-query: select 1\n\nsecurity:\n  oauth2:\n    resource:\n      id: ${spring.application.name}\n      user-info-uri: http://${GATEWAY_HOST:127.0.0.1}:${PORT_GATEWAY:8301}/auth/oath2/info\n\nmybatis-plus:\n  configuration:\n    jdbc-type-for-null: null\n  global-config:\n    banner: false\n\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        exclude: info,health,sentinel\n\nmy:\n  doc:\n    enable: true\n    title: ${spring.application.name}文档\n    description: ${my.doc.title}\n    name: zclcs\n    email: 2371219112@qq.com\n    url: https://github.com/zclcs\n    version: 2.2-RELEASE\n  log:\n    enable-log-for-controller: ${LOG_CONTROLLER_PARAMS:false}', 'ec82d5c12a8a779a6499b556aef311c4', '2022-01-09 21:26:46', '2022-05-23 22:01:42', 'nacos', '192.168.33.1', '', 'dev', '', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (141, 'rabbit-mq.yaml', 'DEFAULT_GROUP', 'spring:\n  rabbitmq:\n    host: ${RABBIT_MQ_HOST:127.0.0.1}\n    port: ${RABBIT_MQ_PORT:5672}\n    username: ${RABBIT_MQ_USERNAME:root}\n    password: ${RABBIT_MQ_PASSWORD:123456}\n    virtual-host: /${NACOS_NAMESPACE:dev}\n    # 手动提交消息\n    listener:\n      type: simple\n      simple:\n        #消息必须手动确认\n        acknowledge-mode: manual\n        #限制每次发送一条数据。\n        prefetch: 1\n        #重试策略相关配置\n        retry:\n          # 开启消费者(程序出现异常)重试机制，默认开启并一直重试\n          enabled: true\n          # 最大重试次数\n          max-attempts: 5\n          # 重试间隔时间(毫秒)\n          initial-interval: 30000\n      direct:\n        acknowledge-mode: manual', '0b5200b1d6d129ae0c76c3eab11cbfaf', '2022-02-06 21:37:27', '2022-05-27 03:56:14', 'nacos', '192.168.33.1', '', 'dev', '', '', '', 'yaml', '');
