INSERT INTO `his_config_info` VALUES (140, 698, 'server.yaml', 'DEFAULT_GROUP', '', 'spring:\n  aop:\n    proxy-target-class: true\n\n  jackson:\n    date-format: yyyy-MM-dd HH:mm:ss\n    time-zone: GMT+8\n\n  freemarker:\n    check-template-location: false\n\n  datasource:\n    dynamic:\n      p6spy: ${LOG_SQL_ENABLED:true}\n      strict: true\n      seata: true    #开启seata代理，开启后默认每个数据源都代理，如果某个不需要代理可单独关闭\n      seata-mode: AT #支持XA及AT模式,默认AT\n      hikari:\n        connection-timeout: 30000\n        max-lifetime: 1800000\n        max-pool-size: 15\n        min-idle: 5\n        connection-test-query: select 1\n\nsecurity:\n  oauth2:\n    resource:\n      id: ${spring.application.name}\n      user-info-uri: http://${GATEWAY_HOST:127.0.0.1}:${PORT_GATEWAY:8301}/auth/oath2/info\n\nmybatis-plus:\n  configuration:\n    jdbc-type-for-null: null\n  global-config:\n    banner: false\n\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        exclude: info,health,sentinel\n\nfeign:\n  hystrix:\n    enabled: true\n  client:\n    config:\n      default:\n        connectTimeout: 30000\n        readTimeout: 30000\n\n#hystrix的超时时间\nhystrix:\n  threadpool:\n    default:\n      coreSize: 10\n  command:\n    default:\n      execution:\n        isolation:\n          thread:\n            timeoutInMilliseconds: 10000\n\nmy:\n  doc:\n    enable: true\n    title: ${spring.application.name}文档\n    description: ${my.doc.title}\n    name: zclcs\n    email: 2371219112@qq.com\n    url: https://github.com/zclcs\n    version: 2.2-RELEASE\n  log:\n    enable-log-for-controller: ${LOG_CONTROLLER_PARAMS:false}', '01ce4638fd9c652343b44888940168ab', '2022-05-24 11:01:42', '2022-05-23 22:01:42', 'nacos', '192.168.33.1', 'U', 'dev');
